

   CREATE TABLE "users" (
  "id_user" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),
  "name_user" varchar(50),
  "email_user" varchar(50) UNIQUE,
  "password_user" varchar(10),
  "status_user" boolean,
  PRIMARY KEY ("id_user")
);

